let url, pengeluaran_jenis = $("#pengeluaran_jenis").DataTable({
    responsive: !0,
    scrollX: !0,
    ajax: readUrl,
    columnDefs: [{
        searcable: !1,
        orderable: !1,
        targets: 0
    }],
    order: [
        [1, "asc"]
    ],
    columns: [{
        data: null
    }, {
        data: "jenis"
    }, {
        data: "action"
    }]
});

function reloadTable() {
    pengeluaran_jenis.ajax.reload()
}

function addData() {
    $.ajax({
        url: addUrl,
        type: "post",
        dataType: "json",
        data: $("#form").serialize(),
        success: () => {
            $(".modal").modal("hide"), Swal.fire("Sukses", "Sukses Menambahkan Data", "success"), reloadTable()
        },
        error: a => {
            console.log(a)
        }
    })
}

function remove(a) {
    Swal.fire({
        title: "Hapus",
        text: "Hapus data ini?",
        type: "warning",
        showCancelButton: !0
    }).then((e) => {
        e.value && $.ajax({
            url: deleteUrl,
            type: "post",
            dataType: "json",
            data: {
                id: a
            },
            success: () => {
                Swal.fire("Sukses", "Sukses Menghapus Data", "success"), reloadTable()
            },
            error: a => {
                console.log(a)
            }
        })
    }, function(dismiss){
                console.log(dismiss)
    });
}

function editData() {
    $.ajax({
        url: editUrl,
        type: "post",
        dataType: "json",
        data: $("#form").serialize(),
        success: () => {
            $(".modal").modal("hide"), Swal.fire("Sukses", "Sukses Mengedit Data", "success"), reloadTable()
        },
        error: a => {
            console.log(a)
        }
    })
}

function add() {
    url = "add", $(".modal-title").html("Tambah Jenis"), $('.modal button[type="submit"]').html("Simpan")
}

function edit(a) {
    $.ajax({
        url: get_jenisUrl,
        type: "post",
        dataType: "json",
        data: {
            id: a
        },
        success: a => {
            $('[name="id"]').val(a.id), $('[name="jenis"]').val(a.jenis), $(".modal").modal("show"), $(".modal-title").html("Edit Jenis"), $('.modal button[type="submit"]').html("Simpan"), url = "edit"
        },
        error: a => {
            console.log(a)
        }
    })
}
pengeluaran_jenis.on("order.dt search.dt", () => {
    pengeluaran_jenis.column(0, {
        search: "applied",
        order: "applied"
    }).nodes().each((a, e) => {
        a.innerHTML = e + 1
    })
}), $("#form").validate({
    errorElement: "span",
    errorPlacement: (a, e) => {
        a.addClass("invalid-feedback"), e.closest(".form-group").append(a)
    },
    submitHandler: () => {
        "edit" == url ? editData() : addData()
    }
}), $(".modal").on("hidden.bs.modal", () => {
    $("#form")[0].reset(), $("#form").validate().resetForm()
});